import './ExpenseItem.css'
// A component in react is just a javascript function
function ExpenseItem(props){
    const expenseDate = new Date(2021, 7, 31);
    // add year then month then numbr of days in that month
    const expenseTitle = "Car Insurance"
    const expenseAmount = "300000"
    return (
        <div className="expense-item">
            <div className="expense-item__description">{props.date.toLocaleString('en-US', {month: 'long'}) }</div>
            <div className="expense-item__description">
                <h2>{props.title}</h2>
                {/* here props.title is {expenses[0].title} defined in App.js */}
                <div className="expense-item__price">{props.amount}</div>
            </div>
        </div>
    )
}

export default ExpenseItem;
